import slugify from 'slugify';
import CustomFeaturesBox from './CustomFeaturesBox/CustomFeaturesBox';
import CartBox from './CartBox/CartBox';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selected: {
        Processor: {
          name: '17th Generation Intel Core HB (7 Core with donut spare)',
          cost: 700
        },
        'Operating System': {
          name: 'Ubuntu Linux 16.04',
          cost: 200
        },
        'Video Card': {
          name: 'Toyota Corolla 1.5v',
          cost: 1150.98
        },
        Display: {
          name: '15.6" UHD (3840 x 2160) 60Hz Bright Lights and Knobs',
          cost: 1500
        }
      }
    };
  }

  //Sets new option that is selected
  updateOption = (event) => {
    const target = event.target;
    const value = target.type === 'radio' ? target.checked : target.value;
    const name = target.name;
    //const featureUpdated = this.state.selected[feature];
    //const targetFeature = this.state.selected[feature];
    //const sourceFeature = this.state.selected.Display;
    //const featureUpdate = Object.assign(targetFeature, sourceFeature);
    this.setState({
      [name]: value
    });
  };

  render() {
