import React from 'react';
import slugify from 'slugify';
import CustomSelections2 from '../CustomSelections2/CustomSelections2';

const USCurrencyFormat = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
});

class CustomIndividualSelections extends React.Component {
    render() {
        console.log(this.props);
        console.log(this.props.selectedFeatures);
        const { selectedFeatures } = this.props;
        featuresAvailable = this.props.featuresAvailable
            .map((feature, idx) => {
                const features = Object.keys(featuresAvailable).map((feature, idx) => {
                    const featureHash = feature + '-' + idx;
                    const options = featuresAvailable[feature].map(item => {
                        const itemHash = slugify(JSON.stringify(item));
                        return (
                            <div key={itemHash} className="feature__item">
                                <input
                                    type="radio"
                                    id={itemHash}
                                    className="feature__option"
                                    name={slugify(feature)}
                                    checked={item.name === this.state.selected[feature].name}
                                    onChange={e => this.updateFeature(feature, item)}
                                />
                                <label htmlFor={itemHash} className="feature__label">
                                    {item.name} ({USCurrencyFormat.format(item.cost)})
                            </label>
                            </div>
                        );
                    });
                });
            });
        return (
            <CustomSelections2 featuresAvailable={this.props.featuresAvailable} />
        );

    };
}

export default CustomIndividualSelections;
