import React from 'react';
import slugify from 'slugify';

const USCurrencyFormat = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
});

class Option extends React.Component {
    render() {
        console.log(this.props);
        console.log(this.props.option.name);
        console.log(this.props.selectedFeatures.name);
        return (
            <div>
                <input
                    type="radio"
                    id={this.props.option.name}
                //className="feature_option"
                //name={slugify(this.props.option.name)}
                //checked={this.props.option.name === this.props.selectedFeatures[this.props.feature].name}
                //onChange={e => this.updateFeature(this.props.feature, this.props.option)}
                />
                <label htmlFor={this.props.option.name} className="feature__label">
                    {this.props.option.name} ({USCurrencyFormat.format(this.props.option.cost)})
                </label>
            </div>
        )
    }
}

export default Option;
